["custom"] = "function(event, ...)\n    -- doesn't do marking if not player lead and \"not lead\" is toggled in custom options\n    if (aura_env.config.requireLead and not UnitIsGroupLeader(\"player\")) then return end \n    \n    --request from automark WA client\n    if event == \"AUTOMARK_REQUEST_MARK_MOB\" and UnitGUID(\"player\") == select(1,...) then\n        local _,unit, order = ...\n        if unit == \"mouseover\" and (not aura_env.config.mouseover or (aura_env.config.mouseoverShiftDown and not IsShiftKeyDown())) then return end\n        if unit ~= \"mouseover\" and not aura_env.config.combatMarking then return end\n        aura_env.markUnit(unit, order)\n    end\n    \n    ---------------------------------------------------------------------------------------\n    --leaving combat resets the tracked marks, but also checks if there are any markers out\n    if event == \"PLAYER_REGEN_ENABLED\" then\n        aura_env.markedMobs = {}\n        aura_env.assignedMarks = {}\n        aura_env.checkPremarkedMobs()\n        aura_env.checkPremarkedPlayers()\n    end\n    --when the player enters combat we do another check of markers, but we do not reset the tracked marks this time\n    if event == \"PLAYER_REGEN_DISABLED\" then\n        aura_env.checkPremarkedMobs()\n        aura_env.checkPremarkedPlayers()\n    end\n    ---------------------------------------------------------------------------------------\n    --Frees up marks when mobs die\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and (select(2,...) == \"UNIT_DIED\" or select(2,...) == \"UNIT_DESTROYED\") then\n        local guid = select(8,...)     \n        if guid and(aura_env.markedMobs[guid]) then\n            local type = select(1, strsplit(\"-\", guid)) \n            \n            if string.lower(type)~=\"player\" then\n                aura_env.assignedMarks[aura_env.markedMobs[guid]] = false\n                aura_env.markedMobs[guid] = nil\n            end\n        end    \n    end\n    \n    \n    ---------------------------------------------------------------------------------------\n    if event == \"READY_CHECK\" or event == \"CHALLENGE_MODE_START\" then  \n        --sets maker for tank and healer if selected in options      \n        aura_env.setTankMark() \n        aura_env.setHealMark()\n        aura_env.markedMobs = {}\n        aura_env.assignedMarks = {}\n    end    \nend\n\n\n\n",
