["init"] = {
["custom"] = "-- [markerIndex] = boolean\naura_env.assignedMarks = {}\n-- [GUID] = markerIndex\naura_env.markedMobs = {}\naura_env.defaultOrder = {1,2,3,4,5,6,7,8}\n\n--checks nameplates and boss frames on screen for markers and adds those markers to the tracked list if not already in it. DOES NOT empty the list of marked mobs or marks in use\naura_env.checkPremarkedMobs = function ()\n    for i = 1,40 do\n        local unit = \"nameplate\"..i\n        if UnitExists(unit) and GetRaidTargetIndex(unit) and not UnitPlayerControlled(unit) then\n            aura_env.assignedMarks[GetRaidTargetIndex(unit)] = true\n            aura_env.markedMobs[UnitGUID(unit)] = GetRaidTargetIndex(unit)\n        end\n    end\n    for i = 1,6 do\n        local unit = \"boss\"..i\n        if UnitExists(unit) and GetRaidTargetIndex(unit) and not UnitPlayerControlled(unit) then\n            aura_env.assignedMarks[GetRaidTargetIndex(unit)] = true\n            aura_env.markedMobs[UnitGUID(unit)] = GetRaidTargetIndex(unit) \n        end\n    end\nend\n--checks marks on players in party. DOES NOT empty the list of marked mobs or marks in use\naura_env.checkPremarkedPlayers = function ()\n    for unit in WA_IterateGroupMembers() do\n        if GetRaidTargetIndex(unit) then\n            aura_env.assignedMarks[GetRaidTargetIndex(unit)] = true\n            aura_env.markedMobs[unit] = GetRaidTargetIndex(unit)\n        end\n    end\nend\n\n--takes in a unit and the order to mark it with. function checks the marks in the order to find the first availible marker from that order\naura_env.markUnit = function (unit, order)\n    local guid = UnitGUID(unit)\n    aura_env.checkPremarkedMobs()\n    aura_env.checkPremarkedPlayers()\n    \n    --special case: if the unit has previously had a marker that has been removed\n    if aura_env.markedMobs[guid] and not GetRaidTargetIndex(unit) then\n        \n        --if the same marker is still is available\n        if aura_env.assignedMarks[aura_env.markedMobs[guid]] and not aura_env.isAnotherGuidAssignedToMark(guid, aura_env.markedMobs[guid]) then\n            aura_env.assignedMarks[aura_env.markedMobs[guid]] = true\n            SetRaidTarget(unit,aura_env.markedMobs[guid])\n            return\n        end\n        --wasn't able to re-apply the same marker due to the marker being in use by another mob. Normal case kicks in and we assign a new marker\n    end\n    \n    --check if any of the prefered marks are available if one is there:\n    if order and type(order) == \"table\" then\n        \n        for i=1, #order do\n            --we check if the order is greater than 0 since a unselected option is 0\n            if order[i] > 0 and not aura_env.assignedMarks[order[i]] and aura_env.config.enabledMarks[order[i]] then\n                aura_env.markedMobs[guid] = order[i]\n                aura_env.assignedMarks[order[i]] = true\n                SetRaidTarget(unit, order[i])\n                return\n            end\n        end\n    end\n    \n    for i=1, #aura_env.defaultOrder do\n        if not aura_env.assignedMarks[aura_env.defaultOrder[i]] and aura_env.config.enabledMarks[aura_env.defaultOrder[i]] then\n            \n            aura_env.markedMobs[guid] = aura_env.defaultOrder[i]\n            aura_env.assignedMarks[aura_env.defaultOrder[i]] = true\n            SetRaidTarget(unit, aura_env.defaultOrder[i])\n            return\n        end\n    end\nend  \n\naura_env.isAnotherGuidAssignedToMark = function (guid, markIndex)\n    for k,v in pairs(aura_env.markedMobs) do \n        if k ~= guid and markIndex == v then \n            return true \n        end\n    end\n    \nend\n\n\n--marks the tank with the mark from config IF they don't have a mark already\naura_env.setTankMark = function()\n    if aura_env.config.markTank == 1 then return end\n    for unit in WA_IterateGroupMembers() do\n        if UnitGroupRolesAssigned(unit) == \"TANK\" and not GetRaidTargetIndex(unit) and not aura_env.assignedMarks[aura_env.config.markTank - 1] then \n            SetRaidTarget(unit, aura_env.config.markTank - 1) \n            return\n        end\n    end\nend\naura_env.setTankMark()\n\n\n--marks the Healer with the mark from config IF they don't have a mark already\naura_env.setHealMark = function()\n    if aura_env.config.markHeal == 1 then return end\n    for unit in WA_IterateGroupMembers() do\n        if UnitGroupRolesAssigned(unit) == \"HEALER\" and not GetRaidTargetIndex(unit) and not aura_env.assignedMarks[aura_env.config.markTank - 1] then \n            SetRaidTarget(unit, aura_env.config.markHeal - 1) \n            return\n        end\n    end    \nend",
