["init"] = {
["custom"] = "aura_env.mystates = {}\naura_env.TextAnchor = WeakAuras.GetData(aura_env.id).subRegions[2].text_anchorPoint\naura_env.AuraWidth = aura_env.region:GetWidth()\naura_env.AuraHeight = aura_env.region:GetHeight()\naura_env.options = {}\nfor _,option in pairs(aura_env.config.spellOptions) do\n    aura_env.options[option.message] = option\nend\naura_env.IDoptions = {}\nfor _,option in pairs(aura_env.config.spellOptions) do\n    aura_env.IDoptions[tostring(option.spellid)] = option\nend\n\n\nfunction aura_env.collisionCalc()\n    -- Refresh and recalculate offset\n    \n    local events={}\n    -- Sort everything by exp\n    local now = GetTime()\n    \n    for state in pairs(aura_env.mystates) do\n        state.c_offset = 0\n        table.insert(events, state)\n    end\n    table.sort(events, function(a, b) return (a.expirationTime or 0)<(b.expirationTime or 0) end)\n    \n    -- Calculate offset for the regluar events to prevent icon collision\n    for i = #events, 1, -1 do\n        local state = events[i]\n        local lastState = events[i+1]\n        if lastState then\n            state.c_offset = (aura_env.AuraHeight+aura_env.config.IconSpacing) - \n            (((lastState.expirationTime or 0) - (state.expirationTime or 0)) *\n                aura_env.config.travelDist / state.trigger.remaining - (lastState.c_offset or 0))\n            -- offset must be positive\n            if state.c_offset < 0 then state.c_offset = 0 end\n        end\n    end\nend\n\n--[[\naura_env.hideDBMBarsHelper=function()\n    if DBM and DBM.Bars then\n        if aura_env.config.hideDBMBars then\n            \n            if not DBT:GetSkins().Jods_Raid_Ability_Timeline_Skin then\n                local skin = DBT:RegisterSkin(\"Jods_Raid_Ability_Timeline_Skin\")\n                skin.Options = {\n                    HugeAlpha = 0.0001,\n                    Alpha = 0.0001,\n                    IconLeft = false,\n                    IconRight = false,\n                    InlineIcons = false,\n                    Bar7CustomInline = false\n                }\n                DBT:SetSkin(\"Jods_Raid_Ability_Timeline_Skin\")\n                DBT:Rearrange()\n            end\n            \n        end\n    end\nend\naura_env.hideDBMBarsHelper()\n\n\naura_env.hideBars = function()\n    if aura_env.config.hideDBMBars\n    and DBM \n    and DBM.Bars\n    and DBM.Bars.numBars then\n        for i = 1, DBM.Bars.numBars do\n            if _G[\"DBT_Bar_\"..i] then\n                _G[\"DBT_Bar_\"..i]:Hide()\n            end\n        end\n    end\nend\n--]]",
