["translateFunc"] = "function(progress, startX, startY, deltaX, deltaY)\n    local endX, endY = startX\n    --aura_env.collisionCalc()\n    if not aura_env.state then\n        endY = startY \n    elseif aura_env.state.paused then\n        local prog=1\n        if aura_env.state.remaining then\n            prog = aura_env.state.remaining/aura_env.state.trigger.remaining \n        end\n        if prog>1 then prog=1\n        elseif prog<0 then prog=0 end\n        if aura_env.TextAnchor == \"OUTER_LEFT\" then\n            endX = startX + aura_env.AuraWidth\n        elseif aura_env.TextAnchor == \"OUTER_RIGHT\" then\n            endX = startX - aura_env.AuraWidth\n        else  \n            print(\"RaidAbilityTimeline -- This Anchor is currently not supported\")\n        end\n        endY = startY - ((1-prog) * aura_env.config.travelDist) - (aura_env.state.c_offset or 0)\n    else\n        -- normal progress\n        local prog = 1\n        if aura_env.state.expirationTime then\n            prog = (aura_env.state.expirationTime - GetTime()) / aura_env.state.trigger.remaining \n        end\n        if prog > 1 then\n            prog = 1\n        elseif prog < 0 then\n            prog = 0\n        end\n        endY = startY - ((1 - prog) * aura_env.config.travelDist) - (aura_env.state.c_offset or 0) -- + aura_env.config.spacing / 4\n    end\n    if aura_env.config.reverse then\n        endY = -aura_env.config.travelDist - endY\n    end\n    return endX, endY\nend",
