["custom"] = "-- print(\"Mana bar:\" .. total_mana_width)\nsleep(2)\naura_env.region:SetWidth(total_mana_width)\nprint(total_mana_width)\nlocal total_active = 0\n\nlocal test = WeakAuras.regions[\"Monk - Main 2\"].region.controlledChildren\nprint(\"---\")\nfor i, k in pairs(test) do\n    for j, l in pairs(k) do\n        if l.regionData.shown then\n            total_active = total_active + 1\n            \n            \n        end\n        \n    end\nend\n\nprint(\"total active\" .. total_active)\nprint(\"---\")\n\n\n",
["do_custom"] = false,
