["init"] = {
["custom"] = "aura_env.spellId = 0\naura_env.active = false\naura_env.max_vitality = 0\n\naura_env.calcVitality = function()\n    local current = 0\n    local aspect;\n    for i = 1, 255 do\n        local auraData = C_UnitAuras.GetBuffDataByIndex(\"player\", i)\n        if auraData and auraData.spellId == aura_env.spellId then\n            aspect = C_TooltipInfo.GetUnitBuffByAuraInstanceID(\"player\", auraData.auraInstanceID)\n        end\n    end\n    if aspect then\n        local tooltipSize = {};\n        if aspect.lines[2] then\n            local tooltipText = aspect.lines[2].leftText\n            for t in tooltipText:gmatch(\"(-?%d[%d%.,]*)\") do\n                if (LARGE_NUMBER_SEPERATOR == \",\") then\n                    t = t:gsub(\",\", \"\");\n                else\n                    t = t:gsub(\"%.\", \"\");\n                    t = t:gsub(\",\", \".\");\n                end\n                tinsert(tooltipSize, tonumber(t));\n            end\n            \n            current = tooltipSize[1]\n        end\n    end\n    \n    return current\nend\n\n\n\n\n",
