["init"] = {
["custom"] = "local aura_env = aura_env\nlocal config = aura_env.config\n\nlocal modifierKeys = {\n    [1] = \"\",\n    [2] = \"SHIFT-\",\n    [3] = \"CTRL-\",\n    [4] = \"ALT-\",\n}\n\nlocal button = _G[\"WorldMarkerCycler\"]\nif not button then\n    button = CreateFrame(\"Button\", \"WorldMarkerCycler\", nil, \"SecureActionButtonTemplate\")\n    button:SetAttribute(\"type\", \"macro\")\n    button:RegisterForClicks(\"AnyUp\", \"AnyDown\")\nend\n\nlocal body = \"i = 0;order = newtable()\"\nfor i = 1, 8 do\n    if config.order[\"mark\"..i] ~= 1 then\n        body = body .. format(\"tinsert(order, %s)\", config.order[\"mark\"..i] - 1)\n    end\nend\nSecureHandlerExecute(button, body)\nSecureHandlerUnwrapScript(button, \"PreClick\")\nSecureHandlerWrapScript(button, \"PreClick\", button, [=[\n        if not down or not next(order) then\n            return\n        end\n\n        if button == \"reset\" then\n            i = 0\n            self:SetAttribute(\"macrotext\", \"/cwm 0\")\n        else\n            i = i%#order + 1\n            self:SetAttribute(\"macrotext\", \"/wm [@cursor]\"..order[i])\n        end\n]=])\n\nlocal frame = _G[\"WorldMarkerCyclerBinding\"] or CreateFrame(\"Frame\", \"WorldMarkerCyclerBinding\")\nClearOverrideBindings(frame)\nfor _, action in ipairs({\"place\", \"reset\"}) do\n    if config[action].key ~= \"\" then\n        SetOverrideBindingClick(frame, true, modifierKeys[config[action].modifier]..config[action].key, button:GetName(), action)\n    end\nend",
["do_custom"] = true,
