["customText"] = "function()\n    if UnitPlayerControlled(\"mouseover\") then\n        local classColor = WA_ClassColorName(\"mouseover\")\n        return classColor\n    else\n        local newName = UnitName(\"mouseover\") or \"\"\n        local red, green, blue, alpha = UnitSelectionColor(\"mouseover\",true)\n        \n        local colorReturn = string.format(\"%02x%02x%02x\", red*255, green*255, blue*255)\n        \n        return \"|cff\"..colorReturn..newName..\"|r\"\n        \n        \n        \n    end\n    \n    \n    \nend\n\n\n",
