["custom"] = "function(s, e, ...)    \n    if e == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local sourceGUID = select(4, ...)\n        if sourceGUID == aura_env.playerGUID then\n            local spellID = select(12, ...)\n            if spellID == 451242 or spellID == 452685 or spellID == 452684 or spellID == 452688 then\n                local subEvent = select(2, ...)\n                if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_REFRESH\" then\n                    if aura_env.saved.deck[spellID] then\n                        aura_env.saved.deck[spellID].active = true\n                        aura_env.saved.deck[spellID].shuffled = false\n                        \n                        if aura_env.deckCount() == 0 then\n                            aura_env.reshuffle()\n                        end\n                        \n                        for spellID in pairs(aura_env.saved.deck) do\n                            s[spellID] = aura_env.updateState(spellID)\n                        end\n                        return true\n                    end\n                end\n                \n                if subEvent == \"SPELL_AURA_REMOVED\" then\n                    if aura_env.saved.deck[spellID] then\n                        aura_env.saved.deck[spellID].active = false\n                    end\n                    \n                    for spellID in pairs(aura_env.saved.deck) do\n                        s[spellID] = aura_env.updateState(spellID)\n                    end\n                    return true\n                end\n            end\n        end\n    end\n    \n    if e == \"STATUS\" then\n        for spellID in pairs(aura_env.saved.deck) do\n            s[spellID] = aura_env.updateState(spellID)\n        end\n        return true\n    end\n    \n    if e == \"PLAYER_ENTERING_WORLD\" then\n        local initialLogin = select(1, ...)\n        if initialLogin then\n            aura_env.reshuffle(true)\n            for spellID in pairs(aura_env.saved.deck) do\n                s[spellID] = aura_env.updateState(spellID)\n            end\n            \n            return true\n        end\n    end\n    \n    --[[ disabled until the spell is hotfixed to reset on raid pull\n    if e == \"ENCOUNTER_START\" then\n        local difficultyID = select(3, ...)\n        if difficultyID then\n            local _, groupType = GetDifficultyInfo(difficultyID)\n            if groupType and groupType == \"raid\" then\n            print(\"reset on \" .. e)\n                aura_env.reshuffle(true)\n            for spellID in pairs(aura_env.saved.deck) do\n                s[spellID] = aura_env.updateState(spellID)\n            end\n            \n            return true\n            end\n        end\n    end\n    ]]\n    \n    if e == \"PLAYER_SPECIALIZATION_CHANGED\" and not (aura_env.currentSpec == GetSpecialization()) then\n        aura_env.reshuffle(true) \n        for spellID in pairs(aura_env.saved.deck) do\n            s[spellID] = aura_env.updateState(spellID)\n        end\n        \n        return true\n    end\n    \n    if e == \"TRAIT_CONFIG_UPDATED\" then\n        aura_env.reshuffle(true)\n        for spellID in pairs(aura_env.saved.deck) do\n            s[spellID] = aura_env.updateState(spellID)\n        end\n        \n        return true\n    end\nend",
