["custom"] = "aura_env.saved = aura_env.saved or {}\naura_env.saved.deck = aura_env.saved.deck or {}\naura_env.playerGUID = UnitGUID(\"player\")\naura_env.currentSpec = GetSpecialization()\n\naura_env.reshuffle = function(hard)\n    hard = hard or false\n    \n    local actives = {}\n    if not hard then\n        for k, v in pairs(aura_env.saved.deck) do\n            if v and v.active then\n                actives[k] = true \n            end \n        end\n    end\n    \n    aura_env.saved.deck = {\n        [451242] = {\n            name = \"Dodge\",\n            color = \"ffd6d6d6\",\n            active = actives[451242] or false,\n            shuffled = true,\n            icon = 642416\n        },\n        [452684] = {\n            name = \"Crit\",\n            color = \"ffd10000\",\n            active = actives[452684] or false,\n            shuffled = true,\n            icon = 878211\n        },\n        [452685] = {\n            name = \"Mastery\", \"ffffdd00\",\n            color = \"ffffdd00\",\n            active = actives[452685] or false,\n            shuffled = true,\n            icon = 574573\n        },\n        [452688] = {\n            name = \"Shadow\",\n            color = \"ff9c35d0\",\n            active = actives[452688] or false,\n            shuffled = true,\n            icon = 1386548\n        }\n    }\nend\n\naura_env.updateState = function(spellID)\n    local buffInfo = aura_env.saved.deck[spellID]\n    if not buffInfo then return end\n    \n    local shouldDisplay = true\n    if aura_env.config.shadowOnly then \n        shouldDisplay = spellID == 452688\n    end\n    \n    if not shouldDisplay then\n        return {\n            show = false,\n            changed = true\n        }\n    else\n        local info = C_UnitAuras.GetPlayerAuraBySpellID(spellID)\n        \n        local spellName\n        if aura_env.config.singleLetter then\n            spellName = strsub(buffInfo.name, 1, 1)\n        else\n            spellName = buffInfo.name\n        end\n        \n        if buffInfo.active then\n            spellName = aura_env.highlight(spellName)\n        end\n        \n        local deckCount = aura_env.deckCount()\n        local chance = 0\n        if deckCount > 0 then\n            chance = (buffInfo.shuffled and not buffInfo.active) and tonumber(string.format(\"%.0f\", 100 / deckCount)) or 0\n        end\n        \n        return {\n            show = aura_env.config.shadowOnly or (buffInfo.active or buffInfo.shuffled),\n            changed = true,\n            active = buffInfo.active,\n            progressType = \"timed\",\n            duration = info and info.duration or 0,\n            expirationTime = info and info.expirationTime or 0,\n            autoHide = false,\n            name = spellName,\n            icon = buffInfo.icon,\n            color = {aura_env.hexToRGB(buffInfo.color)},\n            chance = chance\n        }\n    end\nend\n\naura_env.color = function(text, color)\n    if text and color then\n        return WrapTextInColorCode(text, color)\n    end \nend\n\naura_env.hexToRGB = function(colorString)\n    local a, r, g, b = string.sub(colorString, 1, 2), string.sub(colorString, 3, 4), string.sub(colorString, 5, 6), string.sub(colorString, 7, 8)\n    if a and r and g and b then\n        return tonumber(r, 16) / 255, tonumber(g, 16) / 255, tonumber(b, 16) / 255, tonumber(a, 16) / 255\n    end\nend\n\naura_env.deckCount = function()\n    local c = 0\n    for _, v in pairs(aura_env.saved.deck) do\n        if v.shuffled then\n            c = c + 1\n        end\n    end\n    return c\nend\n\naura_env.count = function()\n    local c = 0\n    for _ in pairs(aura_env.saved.deck) do\n        c = c + 1\n    end\n    return c\nend\n\naura_env.highlight = function(text)\n    return \">\" .. text .. \"<\"\nend\n\nif aura_env.count() == 0 then\n    aura_env.reshuffle(true)\nend\n\naura_env.operators = {\n    [\"<\"] = function(x, y) return x < y end,\n    [\"<=\"] = function(x, y) return x <= y end,\n    [\">\"] = function(x, y) return x > y end,\n    [\">=\"] = function(x, y) return x >= y end,\n    [\"==\"] = function(x, y) return x == y end,\n    [\"!=\"] = function(x, y) return x ~= y end\n}",
["do_custom"] = true,
},
